mapper.select:
with T01 as ( SELECT R.lot_no, R.record_type, R.created_at, CASE WHEN R.record_type = 'PN' THEN R.qty*(-1) -- PIH trả WH WHEN R.record_type = 'XP' THEN R.qty*(-1) -- PIH nhận WH WHEN R.record_type = 'RT' THEN R.qty*(-1) -- PIH nhận WH WHEN R.record_type = 'LRT' THEN R.qty*(-1) -- PIH trả WH END qty FROM pur_wh_records R WHERE 1 = 1 and R.record_type in ('PN', 'XP', 'RT','LRT') and ( DATE_FORMAT(R.created_at, "%Y-%m-%d") >= DATE_FORMAT(#{fromDate}, "%Y-%m-%d") and DATE_FORMAT(R.created_at, "%Y-%m-%d") <= DATE_FORMAT(#{toDate}, "%Y-%m-%d") )
UNION
-- Process recording SELECT M.clotno, M.record_type, M.created_at, (M.qty*-1) qty FROM material_controls M WHERE 1 = 1 and (M.record_type = 'PIC' or M.record_type is null) and ( DATE_FORMAT(M.created_at, "%Y-%m-%d") >= DATE_FORMAT(#{fromDate}, "%Y-%m-%d") and DATE_FORMAT(M.created_at, "%Y-%m-%d") <= DATE_FORMAT(#{toDate}, "%Y-%m-%d") )
UNION
-- Machine OK SELECT D.lot_no, "Machine OK" record_type, D.created_at, D.qty FROM pidvn.defect_records D where D.defect_code in ('PR00','RC000','MD000','EPR000','EMD000','EDC000','IMD000','EMDEB000') and ( DATE_FORMAT(D.created_at, "%Y-%m-%d") >= DATE_FORMAT(#{fromDate}, "%Y-%m-%d") and DATE_FORMAT(D.created_at, "%Y-%m-%d") <= DATE_FORMAT(#{toDate}, "%Y-%m-%d") )
UNION
-- Ton dau ky truoc SELECT D.lot_no, "Inventory" record_type, D.created_at, D.qty FROM pih_inventory_data D where 1 = 1 and D.request_id = #{requestIdKyTruoc}
), -- Data ly thuyet T02 as ( select T01.lot_no, round(sum(T01.qty), 2) sys_qty from T01 group by T01.lot_no ), -- Data thuc te T03 as ( select I.lot_no, I.qty ivt_qty, I.inventory_area from pih_inventory_data I where I.request_id = 9 ), T04 as ( select T02.lot_no, T02.sys_qty, nvl(T03.ivt_qty, 0) ivt_qty, T03.inventory_area from T02 left join T03 on T02.lot_no = T03.lot_no ) select T04.*, (T04.sys_qty - T04.ivt_qty) balance from T04 where 1 = 1
and T04.inventory_area in
#{item}
