mapper.select:
with T01 as ( select A.part_number, B.galileo_name, A.date, A.qty, A.wh_user_code, A.receive_user_code, A.created_at, A.line, A.machine, A.remark, case when A.type = 'XK' then 'Xuất kho' when A.type = 'OUT' then 'Xuất kho' when A.type = 'NK' then 'Nhập kho' end as type from spare_part_records A left join spare_parts B on A.part_number = B.part_number where 1 = 1
and (A.date >= DATE_FORMAT(#{dateRange[0]},'%Y-%m-%d') and A.date <= DATE_FORMAT(#{dateRange[1]},'%Y-%m-%d'))
), T02 as ( SELECT A.pnsucsm galileo_name, A.cdfc factory_code, DATE_FORMAT(A.ymd8ent, '%Y-%m-%d') input_date, DATE_FORMAT(A.ymd8st, '%Y-%m-%d') effective_date, DATE_FORMAT(A.ymd8ed, '%Y-%m-%d') expiry_date, A.ucstdi standard_price, A.cdcur currency_code, A.umstduc unit FROM pidvn.tnmu007_standard_unit_cost A where 1 = 1 order by A.ymd8ent desc ) select T01.*, T02.factory_code, T02.standard_price, T02.currency_code, T02.unit, ROUND((T02.standard_price*T01.qty), 4) total_price from T01 left join T02 on (T01.galileo_name = T02.galileo_name and (T01.date between T02.effective_date and T02.expiry_date))
