mapper.select:
with T01 as ( SELECT ROW_NUMBER() OVER (ORDER BY TimeLog DESC) as RowNum, A.*, CASE WHEN FORMAT(A.TimeLog,'HH:mm') <= '07:30' THEN CAST(FORMAT(DATEADD(DAY, -1, A.TimeLog), 'yyyy-MM-dd') as date ) ELSE CAST(FORMAT(A.TimeLog,'yyyy-MM-dd') as date) END as Date FROM PIDVN_MealRecord A WHERE 1 = 1
and A.CodeEmp = #{empCode}
) SELECT * from T01 WHERE 1 = 1
and (convert(date, T01.Date) >= convert(date, #{dateRange[0]}) and convert(date, T01.Date) <= convert(date,#{dateRange[1]}))
ORDER BY T01.TimeLog DESC

with T01 as ( SELECT A.CodeEmp, A.ProfileName, CASE WHEN A.LineCode = 'PBS2' THEN N'Suất Phụ' WHEN A.LineCode = 'PEOC1' THEN N'Suất Phụ' ELSE N'Suất Chính' END AS TicketType, CASE WHEN FORMAT(A.TimeLog,'HH:mm') <= '07:30' THEN CAST(FORMAT(DATEADD(DAY, -1, A.TimeLog), 'yyyy-MM-dd') as date ) ELSE CAST(FORMAT(A.TimeLog,'yyyy-MM-dd') as date) END as Date FROM PIDVN_MealRecord A WHERE 1 = 1
and A.CodeEmp = #{empCode}
), T02 as ( SELECT T01.CodeEmp, T01.ProfileName, T01.TicketType, COUNT(*) as Total FROM T01 where 1 = 1
and (convert(date, T01.Date) >= convert(date, #{dateRange[0]}) and convert(date, T01.Date) <= convert(date,#{dateRange[1]}))
GROUP BY T01.CodeEmp, T01.ProfileName, T01.TicketType ) SELECT ROW_NUMBER() OVER (ORDER BY T02.CodeEmp DESC) as RowNum, T02.* FROM T02 ORDER BY CodeEmp DESC

WITH T01 as ( -- Dữ liệu ngày công SELECT [CodeEmp], [ProfileName], [NgayCong] , CASE WHEN CaLamViec = '3HCA' THEN 9.5 WHEN CaLamViec = '3HCA1' THEN 9.5 WHEN CaLamViec = '310A' THEN 10 WHEN CaLamViec = '310B' THEN 10 WHEN CaLamViec = '310S' THEN 10 WHEN CaLamViec = '312A' THEN 12 WHEN CaLamViec = '312B' THEN 12 WHEN CaLamViec = '312S' THEN 12 WHEN CaLamViec = '31242A' THEN 12 WHEN CaLamViec = '31242B' THEN 12 WHEN CaLamViec IS NULL THEN 0 ELSE 8 END WorkingTime , [CaLamViec]
FROM [PVG].[dbo].[View_PIDVN_AttendanceDetail] WHERE 1 = 1 -- AND CodeEmp = '3013755' AND FORMAT(NgayCong, 'yyyy-MM') = '2023-11'
UNION ALL
-- Dữ liệu OT SELECT [CodeEmp] , [ProfileName] , [WorkDateRoot] , SUM([ConfirmHours]) WorkingTime , null as CaLamViec FROM [PVG].[dbo].[View_ThongTinOT_PIDVN] WHERE 1 = 1 AND FORMAT(WorkDateRoot, 'yyyy-MM') = '2023-11' -- AND CodeEmp = '3013755' GROUP BY WorkDateRoot, CodeEmp, ProfileName
), T02 as ( SELECT T01.CodeEmp, T01.ProfileName, WorkingTime, CASE WHEN T01.WorkingTime > 11.5 THEN 2 WHEN (T01.WorkingTime >= 8 AND T01.WorkingTime <= 11.5) THEN 1 WHEN T01.WorkingTime = 0 THEN 0 ELSE 0 END AS Ticket FROM T01 ), T03 as ( -- Tổng hợp theo timesheet SELECT T02.CodeEmp, T02.ProfileName, sum(T02.Ticket) Ticket, 'TimeSheet' Type FROM T02 WHERE 1 = 1 -- AND T02.CodeEmp = '3012982' GROUP BY T02.CodeEmp, T02.ProfileName ), T04 AS ( -- Tổng hợp theo Actual SELECT CodeEmp, ProfileName, COUNT(*) Ticket, 'Actual' Type FROM ( SELECT [CodeEmp], [ProfileName], CASE WHEN FORMAT(TimeLog,'HH:mm') <= '07:30' THEN CAST(FORMAT(DATEADD(DAY, -1, TimeLog), 'yyyy-MM-dd') as date ) ELSE CAST(FORMAT(TimeLog,'yyyy-MM-dd') as date) END as Date FROM [PVG].[dbo].[PIDVN_MealRecord] WHERE 1 = 1 AND FORMAT(TimeLog, 'yyyy-MM') = '2023-11' -- AND CodeEmp = '3012982' -- AND (convert(date, TimeLog) >= convert(date, '2023-10-16') and convert(date, TimeLog) <= convert(date, '2023-10-31'))
) A WHERE 1 = 1 -- AND T01.CodeEmp = '3013755' GROUP BY CodeEmp, ProfileName ) SELECT T03.CodeEmp, T03.ProfileName, T03.Ticket AS TicketTimeSheet, T04.Ticket AS TicketActual FROM T03 LEFT JOIN T04 ON T03.CodeEmp = T04.CodeEmp
